      $emit('update:modelValue', ($event.target as SynCombobox).value);
      $emit('syn-input', $event);
    "
    @syn-focus="$emit('syn-focus', $event)"
    @syn-blur="$emit('syn-blur', $event)"
    @syn-show="$emit('syn-show', $event)"
