// Map attributes
const props = defineProps<{
  /**
* The radio group's label.
* Required for proper accessibility.
* If you need to display HTML, use the `label` slot
instead.
 */
  'label'?: SynRadioGroup['label'];

  /**
* The radio groups's help text.
